import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Unstable_Grid2';
import Avatar from '@mui/material/Avatar';
import { Typography, Paper } from '@mui/material';
import TweetFooter from './TweetFooter';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function Tweet() {
  const user = {
    userneme: 'Pupkin',
    fullname: 'Вася Пупкин',
    tweetAvatar: 'wdadsfajsdfa',
    avatarUrl: 'https://i.pinimg.com/736x/a4/24/40/a42440fcb66eb1b0a2d944284dac1431.jpg',
  };
  const text =
    'В Москве прошли трёхсторонние переговоры министров обороны России, Сирии и Турции - обсудили пути разрешения сирийского кризиса и проблему беженцев, совместные усилия по борьбе с экстремистскими группировками в Сирии.';

  return (
    <Paper sx={{ flexGrow: 1, p: 1 }}>
      <Grid
        container
        spacing={0}>
        <Grid xs>
          <Avatar
            alt={`Аватар пользователя ${user.fullname}`}
            src={user.avatarUrl}
          />
        </Grid>
        <Grid xs={11}>
          <Typography gutterBottom>
            <b>{user.fullname}</b>&nbsp;
            <span>@{user.userneme}</span>&nbsp;
            <span>.</span>&nbsp;
            <span>1 ч</span>&nbsp;
          </Typography>
          <Typography gutterBottom>{text}</Typography>
        </Grid>
      </Grid>
      <TweetFooter />
    </Paper>
  );
}
